import React from 'react'
import { Toast, ToastContainer } from 'react-bootstrap'
export default function MyToast(params) {
  return (
        <ToastContainer
          className="p-3"
          position={'bottom-end'}
        >
          <Toast bg = 'dark' show={params.config.show} 
          onClose={params.closeToast} delay={3000} autohide
          >
            <Toast.Header closeButton={false}>
              <strong className="me-auto">{params.config.heading}</strong>
            </Toast.Header>
            <Toast.Body className={'text-white'}>{params.config.message}</Toast.Body>
          </Toast>
        </ToastContainer>
  )
}
