import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios'
import SearchCard from './SearchCard';
import MySpinner from './MySpinner'

export default function SearchModal(params) {
    const [id, setId] = useState('')
    const [donor, setDonor] = useState(null)

    useEffect(()=>{
        async function fetchData(){
            if(id === ''){
                return
            }
            const donor_url = 'http://127.0.0.1:8000/searchdonor'
            const seeker_url = 'http://127.0.0.1:8000/searchseeker'

            try{    
                let donor_response = await axios.post(donor_url, {'ID' : id})
                let fetched_result = {}

                if(donor_response.status == 200){
                    if(Object.keys(donor_response.data).length > 0){
                        fetched_result['Blood Donors'] = donor_response.data
                    }
                    else{
                        fetched_result['Blood Donors'] = null
                    }
                }
                else{
                    setResult(-1)
                }

                let seeker_response = await axios.post(seeker_url, {'ID' : id})

                if(seeker_response.status == 200){
                    if(Object.keys(seeker_response.data).length > 0){
                        fetched_result['Blood Seekers'] = null

                    }
                    else{
                        fetched_result['Blood Seekers'] = null
                    }
                }
                else{
                    setResult(-1)
                }
            }
            catch(err){
                console.log(err);
                setResult(-1)
            }
        }
        fetchData();
    },[id])

  return (
    <Modal className="my-modal"
    {...params}>
        <Modal.Header closeButton>
          <Modal.Title>Search</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Form className="d-flex">
            <Form.Control
              type="number"
              placeholder="Search by ID"
              value={id}
              onChange={(e)=>{setResult(null);setId(e.target.value)}}
            />
            <Button variant="outline-light">Search</Button>
          </Form>
          <div>
            {console.log(result == null, id != '', result, id)}
            {result == null && id != '' ? <MySpinner/> : <SearchCard result={result}/>}
          </div>
        </Modal.Body>
        
      </Modal>
  )
}
