import React from 'react'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { Button, Table } from 'react-bootstrap'
import AddModal from './AddModal'
export default function Contents(params) {
    let base_url = 'http://127.0.0.1:8000'
    let url_post_fix = {
        'Blood Donors' : '/getdonors',
        'Blood Seekers': '/getseekers',
        'Blood Inventory' :'/getinventory'
    }
    let heading = {
        'Blood Donors' : ['ID', 'Name', 'Gender', 'Phone No', 'Blood Group', 'Units'],
        'Blood Seekers': ['ID', 'Name', 'Gender', 'Phone No', 'Blood Group', 'Units'],
        'Blood Inventory' :[ 'Blood Group', 'Units']
    }

    const [data, setData] = useState(null)
    const [showAdd, setAdd] = useState(false)

    function toggel_model(){
        setAdd(!showAdd);
    }

    useEffect(()=>{
        async function hello(){
            try {
                let response = await axios.get(base_url + url_post_fix[params.activeKey])
                if(response.status == 200){
                    setData(response.data)
                }
                else if(response.status == 204){
                    setData([])
                }
                else{
                    setData(null)
                }
            } catch (error) {
                console.log(error)
            }
        }
        hello();
    },[params.activeKey])

    if(data != null && data != []){
        heading = heading[params.activeKey]
    }

  return data != null && heading != null ?
  <>
    <Table striped bordered hover variant="dark">
        <thead>
            <tr>
            {heading.map((heading, index)=>{
                return <th key={index}>{heading}</th>
            })}
            </tr>
        </thead>
        <tbody>
            {
                data.map((record,index)=>{
                    return <tr>
                        {
                            heading.map((value,index1)=>{
                                return <td key={index1}>
                                    {record[value]}
                                </td>
                            })
                        }
                    </tr>
                })
            }
        </tbody>
    </Table>
    <AddModal show={showAdd} onHide></AddModal>
    <Button variant='outline-light' style={{'marginLeft' : '90%'}} 
    disabled={params.activeKey=='Blood Inventory'}>Add Record</Button>
  </> : <>Error Occured</>
}
