import React from 'react'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { Button, Spinner, Table } from 'react-bootstrap'
import AddModal from './AddModal'

export default function Contents(params) {
    const base_url = 'http://127.0.0.1:8000'
    let url_post_fix = {
        'Blood Donors' : '/getdonors',
        'Blood Seekers': '/getseekers',
        'Blood Inventory' :'/getinventory'
    }
    let headingMap = {
        'Blood Donors' : ['ID', 'Date', 'Name', 'Gender', 'Phone No', 'Blood Group', 'Units'],
        'Blood Seekers': ['ID', 'Date','Name', 'Gender', 'Phone No', 'Blood Group', 'Units', 'Status'],
        'Blood Inventory' :['Blood Group', 'Units']
    }

    const [data, setData] = useState(null)
    const [showAdd, setAdd] = useState(false)

    function refreshPage(){
        setData(null)
    }

    function toggel_model(){
        setAdd(!showAdd);
    }

    useEffect(()=>{
        async function fetchData(){
            try {
                let response = await axios.get(base_url + url_post_fix[params.activeKey])
                if(response.status == 200){
                    setData(response.data)
                }
                else if(response.status == 204){
                    setData([])
                }
                else{
                    setData(-1)
                }
            } catch (error) {
                setData(-1)
                console.log(error)
            }
        }
        fetchData();
    },[params.activeKey,5])
    
    let heading = headingMap[params.activeKey]
    return <Spinner variant='light'/>
  return Array.isArray(data) ? <>
    <Table striped bordered hover variant="dark">
        <thead>
            <tr>
            {heading.map((heading, index)=>{
                return <th key={index}>{heading}</th>
            })}
            </tr>
        </thead>
        {data.length > 0 ? <tbody>
            {
                data.map((record,index)=>{
                    return <tr key={index}>
                        {
                            heading.map((value,index1)=>{
                                return <td key={index1}>
                                    {record[value]}
                                </td>
                            })
                        }
                    </tr>
                })
            }
        </tbody> : <h4>No Data Found</h4>}
    </Table>
    <AddModal show={showAdd} onHide={toggel_model} 
    tab={params.activeKey} refreshPage={refreshPage}
    showToast={params.showToast}/>
    <Button variant='outline-light' style={{'marginLeft' : '90%'}} 
    disabled={params.activeKey=='Blood Inventory'} onClick={toggel_model}>Add Record</Button>
  </> : data === -1 ? <h4>An Error happended</h4> : <Spinner variant='light'/>
}
