import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios'

export default function SearchModal(params) {
    const [id, setId] = useState('')
    const [result, setResult] = useState(null)

    useEffect(()=>{
        async function fetchData(){
            const donor_url = 'http://127.0.0.1:8000/searchdonor'
            const seeker_url = 'http://127.0.0.1:8000/searchseeker'

            try{    
                let donor_response = await axios.post(donor_url, {'ID' : id})
                let seeker_response = await axios.post(seeker_url, {'ID' : id})

                if(donor_response.status == 200 && seeker_response.status == 200){
                    if(Object.keys(donor_response.data).length > 0){
                        setResult({
                            ...result,
                            'Blood Donors' : donor_response.data
                        })
                    }
                    else{
                        setResult({
                            ...result,
                            'Blood Donors' : null
                        })
                    }

                    if(Object.keys(donor_response.data).length > 0){
                        setResult({
                            ...result,
                            'Blood Seekers' : seeker_response.data
                        })
                    }
                    else{
                        setResult({
                            ...result,
                            'Blood Seekers' : null
                        })
                    }
                }
                else{
                    setResult(-1)
                }
            }
            catch(err){
                console.log(err);
                
            }
        }
    },[id])

  return (
    <Modal className="my-modal"
    {...params}>
        <Modal.Header closeButton>
          <Modal.Title>Search</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Form className="d-flex">
            <Form.Control
              type="number"
              placeholder="Search by ID"
              value={id}
              onChange={(e)=>{setId(e.target.value)}}
            />
            <Button variant="outline-light">Search</Button>
          </Form>
          <div>
            <h1>hello</h1>
          </div>
        </Modal.Body>
        
      </Modal>
  )
}
