import React from 'react'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { Table } from 'react-bootstrap'
export default function Contents(params) {
    let base_url = 'http://127.0.0.1:8000'
    let url_post_fix = {
        'Blood Donors' : '/getdonors',
        'Blood Seekers': '/getseekers',
        'Blood Inventory' :'/getinventory'
    }
    const [data, setData] = useState(null)
    useEffect(()=>{
        async function hello(){
            try {
                let response = await axios.get(base_url + url_post_fix[params.activeKey])
                if(response.status == 200){
                    setData(response.data)
                }
                else if(response.status == 204){
                    setData([])
                }
                else{
                    setData(null)
                }
            } catch (error) {
                console.log(error)
            }
        }
        hello();
    },[params.activeKey])

  return data != null ?
  <>
    <Table>
        <thead>
            <tr>
            {Object.keys(data[0]).map((heading, index)=>{
                return <th key={index}>{heading}</th>
            })}
            </tr>
        </thead>
        <tbody>
            {
                data.map((record,index)=>{
                    return <tr>
                        {
                            Object.keys
                        }
                    </tr>
                })
            }
        </tbody>
    </Table>
  </> : <>Error Occured</>
}
